import './ExpenseItem.css';
import ExpenseDate from './ExpenseDate';
import Card from '../UI/Card';
import React from 'react';

function ExpenseItem (props) {
    let title = props.expense.title;

    function clickHandler() {
        title = "Updated";
        alert(title);
    }

    // The title data is something which should cause the component to be re-evaluated and re-drawn on the screen when we change the data 
    return (
        <Card className="expense-item">
            {/* Passing date from the prop here */}
            <ExpenseDate date={props.expense.date}/>
            <div className="expense-item__description">

               <h2>{title}</h2>
                <div className="expense-item_price">${props.expense.amount}</div>
            </div>
            <button on onClick={clickHandler}>Change Title</button>
            {/* <button onClick={ function() { alert("Hi I Aman") }}>Change Title</button>
            <button onClick={alertUser()}>Change Title</button> */}
            
        </Card>
    )
}

// function ExpenseItem({title, amount, date}){
